import{_ as r,c as d,o as a,a7 as e}from"./chunks/framework.BK-5gBo2.js";const o="/blog/assets/01.TuSJK_hu.png",h="/blog/assets/02.9mlHBc7W.png",s="/blog/assets/03.Bo5kRMso.png",i="/blog/assets/04.Cy8q_8xd.png",f=JSON.parse('{"title":"Custom Dvorak","description":"Dvorak配列のカスタマイズ","frontmatter":{"date":"2023-12-28T00:00:00.000Z","title":"Custom Dvorak","description":"Dvorak配列のカスタマイズ","tags":["Keyboard","Dvorak"],"prev":{"text":"函館市電LT","link":"/posts/2023/09/18"},"next":{"text":"YAPC::Hiroshima 2024","link":"/posts/2024/02/10"}},"headers":[],"relativePath":"posts/2023/12/28.md","filePath":"posts/2023/12/28.md"}'),l={name:"posts/2023/12/28.md"};function c(n,t,m,b,u,p){return a(),d("div",null,[...t[0]||(t[0]=[e('<h1 id="dvorak" tabindex="-1">Dvorak <a class="header-anchor" href="#dvorak" aria-label="Permalink to &quot;Dvorak&quot;">​</a></h1><p> Dvorakについて考える。</p><h2 id="custom-dvorak" tabindex="-1">Custom Dvorak <a class="header-anchor" href="#custom-dvorak" aria-label="Permalink to &quot;Custom Dvorak&quot;">​</a></h2><p> キーマップ沼にハマり、キーマップを考えるのがめんどくさくなった。<a href="https://www.kaufmann.no/roland/dvorak/" target="_blank" rel="noreferrer">Programmer Dvorak</a>という配列を見つけたので、レイヤーを駆使し、ホームポジションから、手を動かなさくて済むように、カスタムキーマップを考えた。(改良の余地あり)</p><h3 id="レイヤー0" tabindex="-1">レイヤー0 <a class="header-anchor" href="#レイヤー0" aria-label="Permalink to &quot;レイヤー0&quot;">​</a></h3><p> Dvorak配列にする。</p><p><img src="'+o+'" alt="img"></p><h3 id="レイヤー1" tabindex="-1">レイヤー1 <a class="header-anchor" href="#レイヤー1" aria-label="Permalink to &quot;レイヤー1&quot;">​</a></h3><p> 左右クリック、タブ切り替え、拡大縮小...。その他ショートカットキーを配置。<a href="https://karabiner-elements.pqrs.org/" target="_blank" rel="noreferrer">karabiner-elements</a>で上書きしている部分もあります。</p><p><img src="'+h+'" alt="img"></p><h2 id="shortcut-keys-frequently-used-others" tabindex="-1">Shortcut keys (frequently used, others...) <a class="header-anchor" href="#shortcut-keys-frequently-used-others" aria-label="Permalink to &quot;Shortcut keys (frequently used, others...)&quot;">​</a></h2><h3 id="guiで便利なもの" tabindex="-1">GUIで便利なもの <a class="header-anchor" href="#guiで便利なもの" aria-label="Permalink to &quot;GUIで便利なもの&quot;">​</a></h3><table tabindex="0"><thead><tr><th>ショートカットキー</th><th>効果</th></tr></thead><tbody><tr><td>cmd + [</td><td>戻る(ブラウザ)</td></tr><tr><td>cmd + ]</td><td>進む(ブラウザ)</td></tr><tr><td>alt + cmd + →</td><td>右タブ切り替え(ブラウザ)</td></tr><tr><td>alt + cmd + ←</td><td>左タブ切り替え(ブラウザ)</td></tr><tr><td>ctr + →</td><td>仮想デスクトップ切り替え(右)</td></tr><tr><td>ctr + ←</td><td>仮想デスクトップ切り替え(左)</td></tr><tr><td>ctr + ↑</td><td>ウィンドウの切り替え</td></tr><tr><td>ctr + ↓</td><td>ウィンドウの切り替え</td></tr><tr><td>cmd + =</td><td>拡大</td></tr><tr><td>cmd + -</td><td>縮小</td></tr><tr><td>cmd + 0</td><td>比率リセット</td></tr></tbody></table><h3 id="文字列移動" tabindex="-1">文字列移動 <a class="header-anchor" href="#文字列移動" aria-label="Permalink to &quot;文字列移動&quot;">​</a></h3><table tabindex="0"><thead><tr><th>ショートカットキー</th><th>効果</th></tr></thead><tbody><tr><td>cmd + →</td><td>右末尾</td></tr><tr><td>cmd + ←</td><td>左末尾</td></tr><tr><td>cmd + ↑</td><td>上末尾</td></tr><tr><td>cmd + ↓</td><td>下末尾</td></tr><tr><td>opt + →</td><td>右単語</td></tr><tr><td>opt + ←</td><td>左単語</td></tr></tbody></table><h3 id="文字列選択" tabindex="-1">文字列選択 <a class="header-anchor" href="#文字列選択" aria-label="Permalink to &quot;文字列選択&quot;">​</a></h3><table tabindex="0"><thead><tr><th>ショートカットキー</th><th>効果</th></tr></thead><tbody><tr><td>shift + →</td><td>文字列選択(右一文字)</td></tr><tr><td>shift + ←</td><td>文字列選択(左一文字)</td></tr><tr><td>shift + ↑</td><td>文字列選択(上一文字)</td></tr><tr><td>shift + ↓</td><td>文字列選択(上一文字)</td></tr><tr><td>shift + cmd + →</td><td>文字列選択(右末尾)</td></tr><tr><td>shift + cmd + ←</td><td>文字列選択(左末尾)</td></tr><tr><td>shift + cmd + ↑</td><td>文字列選択(上末尾)</td></tr><tr><td>shift + cmd + ↓</td><td>文字列選択(下末尾)</td></tr><tr><td>shift + opt + →</td><td>文字列選択(右単語)</td></tr><tr><td>shift + opt + ←</td><td>文字列選択(左単語)</td></tr></tbody></table><h3 id="other" tabindex="-1">Other <a class="header-anchor" href="#other" aria-label="Permalink to &quot;Other&quot;">​</a></h3><table tabindex="0"><thead><tr><th>変換前</th><th>変換後</th></tr></thead><tbody><tr><td>cmd + b</td><td>cmd + n</td></tr><tr><td>cmd + shift + y</td><td>cmd + shift + t</td></tr><tr><td>cmd + + ctr + f</td><td>cmd + ctr + y</td></tr><tr><td>cmd + shift + &#39;</td><td>cmd + shift + z</td></tr></tbody></table><h3 id="レイヤー2" tabindex="-1">レイヤー2 <a class="header-anchor" href="#レイヤー2" aria-label="Permalink to &quot;レイヤー2&quot;">​</a></h3><p> Layer(2)を押下時。</p><p><img src="'+s+'" alt="img"></p><h3 id="レイヤー3" tabindex="-1">レイヤー3 <a class="header-anchor" href="#レイヤー3" aria-label="Permalink to &quot;レイヤー3&quot;">​</a></h3><p> Layer(3)を押下時。</p><p><img src="'+i+'" alt="img"></p><h2 id="dvorakでqwertyのshortcut-key" tabindex="-1">DvorakでQWERTYのShortcut Key <a class="header-anchor" href="#dvorakでqwertyのshortcut-key" aria-label="Permalink to &quot;DvorakでQWERTYのShortcut Key&quot;">​</a></h2><p> Dvorak を使うとショートカットキーがメチャクチャになる。 対策として、<a href="https://karabiner-elements.pqrs.org/" target="_blank" rel="noreferrer">karabiner-elements</a>で、ショートカットキーを Qwerty と同じにしている。</p><h3 id="変換例-押された時に、上書きされるように設定している" tabindex="-1">変換例(押された時に、上書きされるように設定している) <a class="header-anchor" href="#変換例-押された時に、上書きされるように設定している" aria-label="Permalink to &quot;変換例(押された時に、上書きされるように設定している)&quot;">​</a></h3><table tabindex="0"><thead><tr><th>変換前</th><th>変換後</th></tr></thead><tbody><tr><td>cmd + &#39;</td><td>cmd + z</td></tr><tr><td>cmd + q</td><td>cmd + x</td></tr><tr><td>cmd + j</td><td>cmd + c</td></tr><tr><td>cmd + k</td><td>cmd + v</td></tr><tr><td>cmd + x</td><td>cmd + b</td></tr></tbody></table><h2 id="vim、other-tools" tabindex="-1">Vim、Other Tools <a class="header-anchor" href="#vim、other-tools" aria-label="Permalink to &quot;Vim、Other Tools&quot;">​</a></h2><p> Vim は、気合いで頑張るか、別レイヤーを組むなどの対策</p><h2 id="日本語入力" tabindex="-1">日本語入力 <a class="header-anchor" href="#日本語入力" aria-label="Permalink to &quot;日本語入力&quot;">​</a></h2><p> <code>k</code>を使った入力がかなりきつい。別の入力方式でも良いかも。</p><ul><li>大西配列</li><li>Tomisuke配列</li><li>Astarte配列</li><li>DvorakJP</li></ul>',34)])])}const q=r(l,[["render",c]]);export{f as __pageData,q as default};
