import{_ as t,c as o,o as c,a7 as r}from"./chunks/framework.BK-5gBo2.js";const T=JSON.parse('{"title":"TypeScript トップ型とボトム型","description":"トップ型とボトム型","frontmatter":{"date":"2025-11-29T00:00:00.000Z","title":"TypeScript トップ型とボトム型","description":"トップ型とボトム型","tags":["TypeScript"],"prev":{"text":"TypeScript サブタイプとスーパータイプ","link":"/posts/2025/11/28"},"next":{"text":"TypeScript 複雑な型と互換性","link":"/posts/2025/11/30"}},"headers":[],"relativePath":"posts/2025/11/29.md","filePath":"posts/2025/11/29.md"}'),n={name:"posts/2025/11/29.md"};function d(p,e,s,a,i,_){return c(),o("div",null,[...e[0]||(e[0]=[r('<h1 id="typescript-トップ型とボトム型" tabindex="-1">TypeScript トップ型とボトム型 <a class="header-anchor" href="#typescript-トップ型とボトム型" aria-label="Permalink to &quot;TypeScript トップ型とボトム型&quot;">​</a></h1><p> <code>TypeScript</code>の型システムにおいて、すべての型を包含する最も抽象的な型は<code>unknown</code>型。<code>unknown</code>型の変数にはどんな型の値でも代入可能。<code>unknown</code>型をすべての型のスーパータイプとして見ることで、その振る舞いを直観的に理解可能。</p><p> 逆に、すべての型のサブタイプとなるのは<code>never</code>型。空集合はすべての集合の部分集合であり、<code>never</code>型はこの空集合に相当する。つまり、<code>never</code>型には実際の値が存在せず、どんな値もこの型の変数に代入することはできない。<code>number</code>型と<code>string</code>型のインターセクション型が<code>never</code>型になるのも、<code>number</code>型でありながら<code>string</code>型でもある要素が存在しないため。</p><p> 型システムでは、すべての型を包含する<code>unknown</code>型を<strong>トップ型</strong>と呼び、すべての型に含まれる<code>never</code>型を<strong>ボトム型</strong>と呼ぶ。</p>',4)])])}const u=t(n,[["render",d]]);export{T as __pageData,u as default};
